    const newPose = POSES_BY_LEVEL.find(p => p.level === girl.level);
    if (newPose) girl.unlockedPoses.push(newPose.name);
    alert(`–£–†–û–í–ï–ù–¨ –ü–û–í–´–®–ï–ù –¥–æ ${girl.level}! –û—Ç–∫—Ä—ã—Ç–æ: ${newPose ? newPose.name : '–Ω–æ–≤—ã–µ –Ω–∞–≤—ã–∫–∏'}`);
