function setupEventListeners() {
    // Splash screen dismissal
    const dismissSplash = () => {
        if (elements.splashScreen && elements.splashScreen.style.display !== "none") {
            elements.splashScreen.style.opacity = "0";
            setTimeout(() => {
                elements.splashScreen.style.display = "none";
            }, 1000);
            window.removeEventListener('keydown', dismissSplash);
            window.removeEventListener('click', dismissSplash);
        }
    };
    window.addEventListener('keydown', dismissSplash);
    window.addEventListener('click', dismissSplash);

    elements.restCleanBtn.onclick = restAndClean;
    // Description toggle in sidebar
    if (elements.toggleDescBtn) {
        elements.toggleDescBtn.onclick = () => {
            const desc = elements.charDesc;
            if (desc.style.display === "none") {
                desc.style.display = "block";
                elements.toggleDescBtn.innerText = "[–°–≤–µ—Ä–Ω—É—Ç—å –æ–ø–∏—Å–∞–Ω–∏–µ]";
            } else {
                desc.style.display = "none";
                elements.toggleDescBtn.innerText = "[–ü–æ–∫–∞–∑–∞—Ç—å –æ–ø–∏—Å–∞–Ω–∏–µ]";
            }
        };
    }

    document.querySelectorAll('.switch-card').forEach(card => {
        card.onclick = () => {
            if (!gameState.isMiniGameRunning) {
                gameState.currentGirl = parseInt(card.dataset.id);
                updateUI();
            }
        };
    });

    elements.patrolBtn.onclick = generateNpcs;
    elements.startSexBtn.onclick = startIntimateAct;
    elements.inspectBodyBtn.onclick = showInspection;
    elements.closeInspection.onclick = () => elements.inspectionOverlay.style.display = "none";

    if (elements.proceedToGameBtn) {
        elements.proceedToGameBtn.onclick = () => {
            if (elements.sceneIntroOverlay) elements.sceneIntroOverlay.style.display = "none";
            startMiniGame();
        };
    }

    if (elements.closeFinishSceneBtn) {
        elements.closeFinishSceneBtn.onclick = () => {
            elements.finishSceneOverlay.style.display = "none";
            gameState.isMiniGameRunning = false;
        };
    }

    elements.inspectionOverlay.onclick = (e) => {
        if (e.target === elements.inspectionOverlay) elements.inspectionOverlay.style.display = "none";
    };

    document.querySelectorAll('.detail-btn').forEach(btn => {
        btn.onclick = () => selectDetailPart(btn.dataset.part);
    });

    document.querySelectorAll('.finish-btn').forEach(btn => {
        btn.onclick = () => selectFinish(btn.dataset.finish);
    });

    if (elements.changePoseBtn) {
        elements.changePoseBtn.onclick = changePose;
    }

    window.addEventListener('keydown', (e) => {
        if (!gameState.isMiniGameRunning) return;

        if (e.code === 'KeyP') {
            changePose();
        }

        if (gameState.phase === 'insertion' && e.key === " ") {
            e.preventDefault();
            gameState.phase = 'rhythm';
            gameState.sexSpeed = 1.0;
            gameState.balance = 50;
            elements.insertionPrompt.style.display = "none";
            elements.gameInterface.style.display = "flex";
            return;
        }

        if (gameState.phase === 'rhythm') {
            // Balance
            if (e.key === "ArrowLeft" || e.key === "a") gameState.balance -= 6.0;
            if (e.key === "ArrowRight" || e.key === "d") gameState.balance += 6.0;

            // Speed
            if (e.key === "ArrowUp") { e.preventDefault(); gameState.sexSpeed = Math.min(5, gameState.sexSpeed + 0.2); }
            if (e.key === "ArrowDown") { e.preventDefault(); gameState.sexSpeed = Math.max(0.5, gameState.sexSpeed - 0.2); }

            // Depth
            if (e.key === "w") { e.preventDefault(); gameState.depth = Math.min(100, gameState.depth + 5); }
            if (e.key === "s") { e.preventDefault(); gameState.depth = Math.max(0, gameState.depth - 5); }

            if (e.key === " ") { e.preventDefault(); gameState.sexSpeed *= 0.8; }
        }
    });
}

