function generateIntroText(npc, girl, pose, type) {
    const pSize = npc.penis.toLowerCase();
    const bType = npc.body.toLowerCase();
    const nName = npc.name;
    const gName = girl.name.split(" ")[0];

    // –í—ã–±–∏—Ä–∞–µ–º —Å–ª—É—á–∞–π–Ω—ã–π —Å—Ü–µ–Ω–∞—Ä–∏–π –∏–∑ 30
    const scenarioIndex = Math.floor(Math.random() * INTRO_SCENARIOS.length);
    const narrativeEffect = INTRO_SCENARIOS[scenarioIndex](npc, girl, gName);

    let text = "";

    // 1. –û—Å–Ω–æ–≤–Ω–æ–µ –ø–æ–≤–µ—Å—Ç–≤–æ–≤–∞–Ω–∏–µ (–æ–¥–Ω–∞ –∏–∑ 30 –≤–∞—Ä–∏–∞—Ü–∏–π)
    text += `<div style="margin-bottom:20px; border-left: 3px solid var(--accent-primary); padding-left: 15px;">${narrativeEffect}</div>`;

    // 2. –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –¥–µ—Ç–∞–ª–∏ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –ø–µ—Ä—Å–æ–Ω–∞–∂–∞
    text += `<div style="margin-bottom:20px; font-style: italic; color: #aaa;">${gName} –≤—ã–≥–ª—è–¥–∏—Ç —Å–µ–≥–æ–¥–Ω—è –æ—Å–æ–±–µ–Ω–Ω–æ —ç—Ñ—Ñ–µ–∫—Ç–Ω–æ. ${girl.details.general.substring(0, 150)}...</div>`;

    // 3. –û–ø–∏—Å–∞–Ω–∏–µ –ø–æ–∑—ã
    const poseTexts = {
        "–ú–∏—Å—Å–∏–æ–Ω–µ—Ä—Å–∫–∞—è": `–û–Ω–∞ –ª–æ–∂–∏—Ç—Å—è –Ω–∞ —Å–ø–∏–Ω—É, —à–∏—Ä–æ–∫–æ —Ä–∞–∑–¥–≤–∏–≥–∞—è –ø–µ—Ä–µ–¥ –Ω–∏–º —Å—Ç—Ä–æ–π–Ω—ã–µ –Ω–æ–≥–∏ –∏ –ø–æ–¥—Ç—è–≥–∏–≤–∞—è –∫–æ–ª–µ–Ω–∏ –∫ –≥—Ä—É–¥–∏, –ø–æ–ª–Ω–æ—Å—Ç—å—é –æ—Ç–∫—Ä—ã–≤–∞—è—Å—å –¥–ª—è –ø–µ—Ä–≤–æ–≥–æ –≥–ª—É–±–æ–∫–æ–≥–æ –ø—Ä–æ–Ω–∏–∫–Ω–æ–≤–µ–Ω–∏—è. –ï—ë –±–µ–¥—Ä–∞ –Ω–∞—á–∏–Ω–∞—é—Ç –Ω–µ–ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω–æ –ø–æ–∫–∞—á–∏–≤–∞—Ç—å—Å—è –≤ —Ç–∞–∫—Ç –µ–≥–æ –¥—ã—Ö–∞–Ω–∏—é.`,
        "–î–æ–≥–≥–∏-—Å—Ç–∞–π–ª": `–û–Ω–∞ –≤—Å—Ç–∞–µ—Ç –Ω–∞ —á–µ—Ç–≤–µ—Ä–µ–Ω—å–∫–∏, —Å–µ–∫—Å—É–∞–ª—å–Ω–æ –≤—ã–≥–∏–±–∞—è —Å–ø–∏–Ω—É –∏ –≤—ã—Å—Ç–∞–≤–ª—è—è —Å–≤–æ–∏ –±–µ–∑—É–ø—Ä–µ—á–Ω—ã–µ, —É–ø—Ä—É–≥–∏–µ —è–≥–æ–¥–∏—Ü—ã –ø—Ä—è–º–æ –ø–µ—Ä–µ–¥ –ª–∏—Ü–æ–º <b>${nName}</b>. –û–Ω–∞ –±—Ä–æ—Å–∞–µ—Ç –∏–≥—Ä–∏–≤—ã–π –≤–∑–≥–ª—è–¥ —á–µ—Ä–µ–∑ –ø–ª–µ—á–æ, –ø—Ä–∏–≥–ª–∞—à–∞—è –µ–≥–æ –∫ –¥–µ–π—Å—Ç–≤–∏—é.`,
        "–ù–∞–µ–∑–¥–Ω–∏—Ü–∞": `–û–Ω–∞ –≥—Ä–∞—Ü–∏–æ–∑–Ω–æ –æ–ø—É—Å–∫–∞–µ—Ç—Å—è —Å–≤–µ—Ä—Ö—É, –Ω–∞–ø—Ä–∞–≤–ª—è—è –µ–≥–æ –Ω–∞–ø—Ä—è–∂–µ–Ω–Ω—ã–π —á–ª–µ–Ω –≤ —Å–≤–æ–µ –≥–æ—Ä—è—á–µ–µ –∏ –≤–ª–∞–∂–Ω–æ–µ –Ω—É—Ç—Ä–æ. –û–Ω–∞ –∑–∞–º–∏—Ä–∞–µ—Ç –Ω–∞ –º–≥–Ω–æ–≤–µ–Ω–∏–µ, –Ω–∞—Å–ª–∞–∂–¥–∞—è—Å—å –º–æ–º–µ–Ω—Ç–æ–º –≤—Ö–æ–¥–∞, –ø—Ä–µ–∂–¥–µ —á–µ–º –Ω–∞—á–∞—Ç—å —Å–≤–æ–π —Ç–∞–Ω–µ—Ü.`,
        "–ê–Ω–∞–ª—å–Ω—ã–π —Å–µ–∫—Å": `–û–Ω–∞ –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –ø–æ–∑—É, –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω–Ω—É—é –¥–ª—è —Å–∞–º–æ–≥–æ —Ç–µ—Å–Ω–æ–≥–æ –∏ –∑–∞–ø—Ä–µ—Ç–Ω–æ–≥–æ –∫–æ–Ω—Ç–∞–∫—Ç–∞, –≥–æ—Ç–æ–≤—è —Å–≤–æ–π —Ç—É–≥–æ–π –∏ –Ω–µ–∂–Ω—ã–π –ø—Ä–æ—Ö–æ–¥. –û–Ω–∞ —Å–ª–µ–≥–∫–∞ –ø–æ–∫—É—Å—ã–≤–∞–µ—Ç –≥—É–±—É, –ø—Ä–µ–¥–≤–∫—É—à–∞—è –æ—Å—Ç—Ä–æ—Ç—É –Ω–æ–≤—ã—Ö –æ—â—É—â–µ–Ω–∏–π.`
    };

    text += `<div style="margin-bottom:20px;"><span style="color:var(--accent-secondary); font-weight:800;">–ü–û–ó–ò–¶–ò–Ø: ${pose.toUpperCase()}</span><br>`;
    text += `–û–Ω–∏ –ø–µ—Ä–µ—Ö–æ–¥—è—Ç –∫ –≤—ã–±—Ä–∞–Ω–Ω–æ–π –ø–æ–∑–µ. ${poseTexts[pose] || "–û–Ω–∏ –∑–∞–Ω–∏–º–∞—é—Ç –≤—ã–±—Ä–∞–Ω–Ω—É—é –ø–æ–∑–∏—Ü–∏—é, –≥–æ—Ç–æ–≤—è—Å—å –∫ –∞–∫—Ç—É —Å –ø–æ–ª–Ω–æ–π —Å–∞–º–æ–æ—Ç–¥–∞—á–µ–π."}`;
    text += `<br><br><i style="color:var(--accent-primary);">${type} –∫–æ–Ω—Ç–∞–∫—Ç —Å–∫–æ—Ä–æ –Ω–∞—á–Ω–µ—Ç—Å—è.</i></div>`;

    text += `<p style="text-align:center; color:var(--accent-gold); font-size:1.4rem; margin-top:20px; font-weight:800; text-shadow: 0 0 10px var(--accent-gold);">–ü–†–ò–ì–û–¢–û–í–¨–¢–ï–°–¨...</p>`;

    return text;
}

// Re-add missing helper functions since I'm rewriting a lot
function showSelectionMenu(npc) {
    gameState.activeNpc = npc;
    const girl = GIRLS_DATA[gameState.currentGirl];
    elements.selectionOverlay.style.display = "block";
    elements.poseSelectionList.innerHTML = "";
    elements.typeSelectionArea.style.display = "none";
    elements.startSexBtn.style.display = "none";
    girl.unlockedPoses.forEach(poseName => {
        const btn = document.createElement('button');
        btn.className = "pose-btn";
        btn.innerText = poseName;
        btn.onclick = () => selectPose(poseName, btn);
        elements.poseSelectionList.appendChild(btn);
    });
}
function selectPose(poseName, btn) {
    gameState.activePose = poseName;
    document.querySelectorAll('.pose-btn').forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
    const poseData = POSES_BY_LEVEL.find(p => p.name === poseName) || { types: ["–í–∞–≥–∏–Ω–∞–ª—å–Ω—ã–π"] };
    elements.typeSelectionArea.style.display = "block";
    elements.typeSelectionList.innerHTML = "";
    poseData.types.forEach(typeName => {
        const tBtn = document.createElement('button');
        tBtn.className = "type-btn";
        tBtn.innerText = typeName;
        tBtn.onclick = () => selectSexType(typeName, tBtn);
        elements.typeSelectionList.appendChild(tBtn);
    });
}
function selectSexType(typeName, btn) {
    gameState.activeSexType = typeName;
    document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
    elements.startSexBtn.style.display = "inline-block";
}
function startIntimateAct() {
    elements.selectionOverlay.style.display = "none";
    const npc = gameState.activeNpc;
    const girl = GIRLS_DATA[gameState.currentGirl];

    // Generate and show the intro story
    const introText = generateIntroText(npc, girl, gameState.activePose, gameState.activeSexType);
    elements.introContent.innerHTML = introText;
    elements.sceneIntroOverlay.style.display = "block";
}
function selectFinish(finishKey) {
    gameState.activeFinish = finishKey;
    elements.finishOverlay.style.display = "none";
    const npc = gameState.activeNpc;
    const girl = GIRLS_DATA[gameState.currentGirl];
    const items = FINISH_PHRASES[finishKey];
    const item = items[Math.floor(Math.random() * items.length)];

    // If it's a function, call it. If string, use as is.
    const phrase = (typeof item === 'function') ? item(npc, girl) : item;
    finishStats(phrase);
}

function restAndClean() {
    const girl = GIRLS_DATA[gameState.currentGirl];
    girl.grooming = 100;
    girl.cleanliness = 100;
    girl.pain = { vaginal: 0, anal: 0, oral: 0 };
    girl.traces = [];
    alert(`${girl.name} –ø—Ä–∏–≤–µ–ª–∞ —Å–µ–±—è –≤ –ø–æ—Ä—è–¥–æ–∫ –∏ –æ—Ç–¥–æ—Ö–Ω—É–ª–∞.`);
    updateUI();
}

